Widget buildListItem(
    String name, String imgpth, String price, Color bgcolor, Color textColor) {
  return Padding(
    padding: const EdgeInsets.only(left: 15.0),
    child: InkWell(
      onTap: () {
        // Navigator.of(BuildContext context).push(MaterialPageRoute(
        //     builder: (context) =>
        //         Iphone(name: name, herotag: name, price: price)));
      },
      child: Container(
        height: 175.0,
        width: 150.0,
        decoration: BoxDecoration(
          borderRadius: BorderRadius.circular(15.0),
          color: bgcolor,
        ),
        child: Column(
          mainAxisAlignment: MainAxisAlignment.center,
          // ignore: prefer_const_literals_to_create_immutables
          children: <Widget>[
            Hero(
              tag: name,
              child: Container(
                height: 75.0,
                width: 75.0,
                decoration: const BoxDecoration(
                  color: Colors.white,
                  shape: BoxShape.circle,
                ),
                child: Center(
                  child: Image.network(
                    imgpth,
                    height: 50.0,
                    width: 50.0,
                  ),
                ),
              ),
            ),
            // ignore: prefer_const_constructors
            SizedBox(
              height: 25.0,
            ),
            Text(
              name,
              style: TextStyle(
                fontSize: 17.0,
                color: textColor,
              ),
            ),
            Text(
              '\$' + price,
              style: TextStyle(
                fontSize: 17.0,
                color: textColor,
              ),
            )
          ],
        ),
      ),
    ),
  );
}
